import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import SignUp from "./Signup";
import Login from "./Login"; // Assuming you have a Login component

const Navbar = ({ loggedIn, email, onLogout }) => {
  const [showSignup, setShowSignup] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const navigate = useNavigate();

  const onSignupClick = () => {
    setShowSignup(!showSignup);
    setShowLogin(false);
  };

  const onLoginClick = () => {
    setShowLogin(!showLogin);
    setShowSignup(false);
  };

  const closeForms = () => {
    setShowSignup(false);
    setShowLogin(false);
    navigate(-1);
  };

  return (
    <nav className="bg-gray-800 p-4 flex justify-between items-center relative">
      <div>
        <Link to="/" className="text-white text-lg font-semibold">
          DocTalk
        </Link>
      </div>
      <div className="flex items-center">
        {!loggedIn ? (
          <>
            <button onClick={onLoginClick} className="text-white mr-4">
              Login
            </button>
            <button onClick={onSignupClick} className="text-white">
              SignUp
            </button>
          </>
        ) : (
          <div className="text-white mr-4">
            Logged in as {email}
            <button onClick={onLogout} className="ml-2">
              Logout
            </button>
          </div>
        )}
      </div>
      {showSignup && (
        <div className="fixed top-16 right-4">
          <SignUp />
          <button onClick={closeForms} className="text-sm text-blue-500 underline mt-4">
            Close
          </button>
        </div>
      )}
      {showLogin && (
        <div className="fixed top-16 right-4">
          <Login />
          <button onClick={closeForms} className="text-sm text-blue-500 underline mt-4">
            Close
          </button>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
