import React, { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = ({ loggedIn, email, onLogout }) => {
    const [showSignup, setShowSignup] = useState(false);

    const onSignupClick = () => {
        setShowSignup(true);
    };

    const closeSignup = () => {
        setShowSignup(false);
    };

    return (
        <nav className="bg-gray-800 p-4 flex justify-between items-center">
            <div>
                <Link to="/" className="text-white text-lg font-semibold">
                    DocTalk
                </Link>
            </div>
            <div className="flex items-center">
                {!loggedIn ? (
                    <>
                        <Link to="/login" className="text-white mr-4">
                            Login
                        </Link>
                        <button onClick={onSignupClick} className="text-white">
                            Sign Up
                        </button>
                    </>
                ) : (
                    <div className="text-white mr-4">
                        Logged in as {email}
                        <button onClick={onLogout} className="ml-2">
                            Logout
                        </button>
                    </div>
                )}
            </div>
            {showSignup && (
                <div className="absolute top-0 left-0 w-full h-full bg-gray-900 bg-opacity-50 flex items-center justify-center">
                    <div className="bg-white p-4 rounded shadow-md">
                        <h2 className="text-lg font-semibold mb-4">Sign Up</h2>
                        {/* Your Signup Form Fields */}
                        {/* Include form fields here (e.g., username, email, password, confirm password) */}
                        {/* Ensure you handle form submission and close the signup form */}
                        <button onClick={closeSignup} className="text-sm text-blue-500 underline mt-2">
                            Close
                        </button>
                    </div>
                </div>
            )}
        </nav>
    );
};

export default Navbar;
