import React from "react";
import { useNavigate } from "react-router-dom";
impor

export const Home = (props) => {
    const { loggedIn, email } = props;
    const navigate = useNavigate();

    const onButtonClick = () => {
        if (loggedIn) {
            localStorage.removeItem("user");
            props.setLoggedIn(false);
        } else {
            navigate("/login");
        }
    };

    return <div className="mainContainer w-full h-screen flex flex-col items-center justify-center bg-cover bg-center"
    style={{ backgroundImage: "url('./assets/loginimg.jpg')" }}>
        <div className={"titleContainer text-center"}>
            <div>
             <h3 className="text-4xl font-bold mb-4">Welcome to DocTalk!</h3>
             </div>
        </div>
        <div className="text-center text-lg mb-8">
            This is the home page.
        </div>
        <div className={"buttonContainer"}>
            <input
                className={"inputButton"}
                type="button"
                onClick={onButtonClick}
                value={loggedIn ? "Log out" : "Log in"} />
            {(loggedIn ? <div>
                Your email address is {email}
            </div> : <div />)}
        </div>


    </div>;
};
export default Home;
