import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export const Home = (props) => {
    const { loggedIn, email } = props;
    const navigate = useNavigate();
    const [showSignup, setShowSignup] = useState(false);
    const [signupData, setSignupData] = useState({
        username: "",
        email: "",
        password: "",
        confirmPassword: "",
    });

    const onButtonClick = () => {
        if (loggedIn) {
            localStorage.removeItem("user");
            props.setLoggedIn(false);
        } else {
            navigate("/login");
        }
    };

    const onSignupClick = () => {
        setShowSignup(true);
    };

    const onSignupSubmit = (e) => {
        e.preventDefault();
        // Handle signup form submission logic here
        // For example: validate fields, create new user, etc.
        console.log("Signup form submitted with data:", signupData);
        // After signup logic, you might want to clear the form or handle success
        setShowSignup(false);
        setSignupData({
            username: "",
            email: "",
            password: "",
            confirmPassword: "",
        });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setSignupData({
            ...signupData,
            [name]: value,
        });
    };

    return (
        <div className="mainContainer w-full h-screen flex flex-col items-center justify-center bg-cover bg-center" style={{ backgroundImage: "url('/path/to/your/image.jpg')" }}>
            <div className="titleContainer text-center">
                <h3 className="text-4xl font-bold mb-4">Welcome to DocTalk!</h3>
            </div>
            <div className="text-center text-lg mb-8">
                This is the home page.
            </div>
            <div className="buttonContainer flex flex-col items-center">
                <input
                    className="inputButton bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mb-4"
                    type="button"
                    onClick={onButtonClick}
                    value={loggedIn ? "Log out" : "Log in"}
                />
                {!loggedIn && (
                    <button
                        className="inputButton bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
                        onClick={onSignupClick}
                    >
                        Sign Up
                    </button>
                )}
                {loggedIn && (
                    <div className="mt-4">
                        Your email address is {email}
                    </div>
                )}
            </div>
            {showSignup && (
                <form className="flex flex-col items-center" onSubmit={onSignupSubmit}>
                    <input
                        type="text"
                        placeholder="Username"
                        name="username"
                        value={signupData.username}
                        onChange={handleInputChange}
                        className="inputField mb-2"
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        name="email"
                        value={signupData.email}
                        onChange={handleInputChange}
                        className="inputField mb-2"
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={signupData.password}
                        onChange={handleInputChange}
                        className="inputField mb-2"
                    />
                    <input
                        type="password"
                        placeholder="Confirm Password"
                        name="confirmPassword"
                        value={signupData.confirmPassword}
                        onChange={handleInputChange}
                        className="inputField mb-2"
                    />
                    <button
                        type="submit"
                        className="inputButton bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                    >
                        Sign Up
                    </button>
                </form>
            )}
        </div>
    );
};

export default Home;
