import React, { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = ({ loggedIn, email, onLogout }) => {
    const [showLogin, setShowLogin] = useState(false);
    const [showSignup, setShowSignup] = useState(false);

    const openLogin = () => {
        setShowLogin(true);
    };

    const closeLogin = () => {
        setShowLogin(false);
    };

    const openSignup = () => {
        setShowSignup(true);
    };

    const closeSignup = () => {
        setShowSignup(false);
    };

    return (
        <nav className="bg-gray-800 p-4 flex justify-between items-center relative">
            <div>
                <Link to="/" className="text-white text-lg font-semibold">
                    DocTalk
                </Link>
            </div>
            <div className="flex items-center">
                {!loggedIn ? (
                    <>
                        <button onClick={openLogin} className="text-white mr-4">
                            Login
                        </button>
                        <button onClick={openSignup} className="text-white">
                            Sign Up
                        </button>
                    </>
                ) : (
                    <div className="text-white mr-4">
                        Logged in as {email}
                        <button onClick={onLogout} className="ml-2">
                            Logout
                        </button>
                    </div>
                )}
            </div>
            {showLogin && (
                <div className="absolute top-16 right-4 bg-white p-6 rounded shadow-md w-80">
                    {/* Login form */}
                    {/* ... */}
                    <button onClick={closeLogin} className="text-sm text-blue-500 underline mt-4">
                        Close
                    </button>
                </div>
            )}
            {showSignup && (
                <div className="absolute top-16 right-4 bg-white p-6 rounded shadow-md w-80">
                    {/* Signup form */}
                    {/* ... */}
                    <button onClick={closeSignup} className="text-sm text-blue-500 underline mt-4">
                        Close
                    </button>
                </div>
            )}
        </nav>
    );
};

export default Navbar;
